mapper.update:
update good_images
set state = 1
where good_id = #{goodId}

mapper.select:
select good_id, seller_open_id, nick_name, gender, avatar_url, campus, college, title, content, phone_number, weixin_number, qq_number, create_time, price, old_price, section,state
from good_info
where good_id=#{goodId}

mapper.update:
update good set
state = #{state}
where good_id = #{goodId}

